import styled from 'styled-components';
import background from '../../assets/backgroundlogin.png';

export const Wrapper = styled.div`
  display: flex;
  width: 100%;
  height: 100%;
  position: static;
`;

export const Container1 = styled.div`
  font-family: Ubuntu;
  display: flex;
  flex-direction: column;
  align-items: center;
  background: #f5f6fa;
  max-height: 100%;
  min-width: 55%;
  max-width: 55%;
  overflow: hidden;

  form {
    display: flex;
    flex-direction: column;
    input {
      font-size: 15px;
      background: #ffffff;
      box-shadow: 0px 10px 20px rgba(180, 185, 207, 0.2);
      border: none;
      border-radius: 7px;
      height: 60px;
      width: 498px;
      text-indent: 15px;
      margin-bottom: 20px;
    }

    div {
      margin-top: 10px;
      display: flex;
      justify-content: space-between;
      align-items: center;

      button {
        font-weight: bold;
        font-size: 15px;
        color: #ffff;
        letter-spacing: 0.15em;
        width: 150px;
        height: 54px;
        background: #d97035;
        border-radius: 7px;
        border: none;
      }

      p {
        font-size: 15px;
      }

      a {
        text-decoration: none;
        color: #d97035;
      }
    }
  }
`;

export const Container2 = styled.div`
  font-family: Ubuntu;
  background: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)),
    url(${background});

  width: 45%;
  display: flex;
  flex-direction: column;

  #h1 {
    margin-top: 500px;
    margin-left: 100px;
    letter-spacing: 0.15em;
    font-size: 17px;
    width: 425px;
    h1 {
      color: #ffffff;
    }
  }

  #p {
    margin-left: 100px;
    margin-top: 10px;
    p {
      color: #ffffff;
      font-size: 18px;
      width: 425px;
    }
    button {
      font-weight: bold;
      letter-spacing: 0.15em;
      margin-top: 50px;
      width: 300px;
      height: 60px;
      background: #d97035;
      border-radius: 7px;
      color: #ffffff;
      border: none;
    }
  }
`;

export const Logo = styled.img`
  width: 167px;
  margin-bottom: 60px;
  margin-top: 25%;
`;
