import styled from 'styled-components';
import background from '../../assets/backgroundlogin.png';
import px2vw from '../../utils/px2vw';

export const Wrapper = styled.div`
  display: flex;
  width: 100%;
  height: 100%;
  position: static;

  @media (max-width: 768px) {
    flex-direction: column-reverse;
  }
`;

export const Container1 = styled.div`
  font-family: Ubuntu;
  display: flex;
  flex-direction: column;
  align-items: center;
  background: #f5f6fa;
  max-height: 100%;
  min-height: 100%;
  min-width: 55%;
  max-width: 55%;
  overflow: hidden;

  @media (max-width: 768px) {
    min-width: 100%;
    max-width: 100%;
    min-height: 55%;
    max-height: 55%;
  }

  form {
    display: flex;
    flex-direction: column;

    @media (max-width: 768px) {
      margin-left: 50%;
    }
    input {
      font-size: 15px;
      background: #ffffff;
      box-shadow: 0px 10px 20px rgba(180, 185, 207, 0.2);
      border: none;
      border-radius: 7px;
      height: 60px;
      width: 498px;
      text-indent: 15px;
      margin-bottom: 20px;

      @media (max-width: 768px) {
        width: 200px;
      }
    }
  }
`;
export const FooterForm = styled.div`
  margin-top: 10px;
  display: flex;
  justify-content: space-between;
  align-items: center;

  button {
    font-weight: bold;
    font-size: 15px;
    color: #ffff;
    letter-spacing: 0.15em;
    width: 150px;
    height: 54px;
    background: #d97035;
    border-radius: 7px;
    border: none;
  }

  p {
    font-size: 15px;
  }

  a {
    text-decoration: none;
    color: #d97035;
  }
`;

export const Logo = styled.img`
  width: 167px;
  margin-bottom: 60px;
  margin-top: 25%;
`;
